        setInput({
            ...input,
            [e.target.name]: e.target.value
        })

        setErrors(validation({
            ...input,
            [e.target.name]: e.target.value
        }))
    }

    function handleSelect(e){
        if (!input.temperament.includes(e.target.value)){
            setInput({
                ...input,
                temperament: [...input.temperament, e.target.value]
            })
        }
    }

